	@Test
	public void dataBlockLe8k26ai() {

		var orl = OraCdcRedoLog.getLinux19c();
		
		var baColb = hexToRaw("");
		var rrColb = new OraCdcRedoRecord(orl, 0x0000000000328c0cl, "0x000014.00027784.0114", baColb);

		assertFalse(rrColb.has5_1());
		assertTrue(rrColb.hasColb());
		assertFalse(rrColb.hasKrvDlr10());
		assertEquals(rrColb.changeColb().obj(), 75656);
		assertEquals(rrColb.changeColb().conId(), 1);
		assertEquals(rrColb.changeColb().qmRowCount(), 9);

		System.out.println(rrColb);
	}
	
